mapper.select:
SELECT COUNT(member_id)
  FROM MEMBER
 WHERE member_id = #{memberId}

SELECT COUNT(member_email)
  FROM MEMBER
 WHERE member_email = #{email}

SELECT COUNT(member_ph)
  FROM MEMBER
 WHERE member_ph = #{phoneNumber}

SELECT MEMBER_NAME AS name, MEMBER_ID AS memberId, MEMBER_PW AS password, MEMBER_EMAIL AS email, MEMBER_PH AS phoneNumber FROM MEMBER
WHERE MEMBER_ID = #{memberId}
WHERE MEMBER_EMAIL = #{email}
WHERE email = '!@#$'

SELECT password
  FROM MEMBER
 WHERE member_id = #{memberId}
