mapper.update:
    UPDATE SHARES
       SET group_yn = #{groupYn}	
     WHERE reserved_number IN (
	 		SELECT t.reserved_number
	 		  FROM TICKET t, SHARES s
	 		 WHERE t.reserved_number = #{reservedNumber}
	 		   AND t.reserved_number = s.reserved_number
	 		   AND s.group_yn = 'Y'
	 		   AND s.owner_yn = 'Y'
	 		   AND s.share_id = #{memberId}
	 )

UPDATE TICKET
   SET cancel = #{cancel}
 WHERE reserved_number IN (
 		SELECT t.reserved_number
 		  FROM TICKET t, SHARES s
 		 WHERE t.reserved_number = #{reservedNumber}
 		   AND t.reserved_number = s.reserved_number
 		   AND s.group_yn = 'N'
 		   AND s.owner_yn = 'Y'
 		   AND s.share_id = #{memberId}
 )
